CREATE TABLE MENU_USER_RATING_MAPPING(MENU_USER_RATING_MAPPING_ID INT NOT NULL, ITEM_ID INT NOT NULL, USER_ID INT NOT NULL, RATING INT, PRIMARY KEY (MENU_USER_RATING_MAPPING_ID), FOREIGN KEY (ITEM_ID) REFERENCES MENU_MASTER(ITEM_ID), FOREIGN KEY (USER_ID) REFERENCES USER(USER_ID));
CREATE TABLE MENU_RATING(MENU_RATING_ID INT NOT NULL, ITEM_ID INT NOT NULL, COUNT_OF_PEOPLE_WHO_HAVE_RATED INT NOT NULL, CURRENT_AVERAGE_RATING INT, PRIMARY KEY (MENU_RATING_ID), FOREIGN KEY (ITEM_ID) REFERENCES MENU_MASTER(ITEM_ID));